'use strict';const foo=()=>{const btnEnviar=document.getElementById('submitButton'); const nameInput=document.getElementById('nameInput'); const lastNameInput=document.getElementById('lastNameInput'); const emailInput=document.getElementById('emailInput'); btnEnviar.addEventListener('click', (e)=>{e.preventDefault(); let dataToSend=getDataFromInputs(); const inputValidation=validateInputs(); if (inputValidation){postNewDataAsync(dataToSend).then(cleanInputs).catch(console.log);}else{Swal.fire({icon: 'error', title: 'Oops...', text: '¡Todos los campos son obligatorios!',});}}); const validateInputs=()=>{return true ? nameInput.value.length !==0 && lastNameInput.value.length !==0 && emailInput.value.length !==0 : false;}; const getDataFromInputs=()=>{let name=nameInput.value; let lastName=lastNameInput.value; let email=emailInput.value; const data={name, lastName, email,}; return data;}; const postNewDataAsync=async (data={})=>{try{const url='http://localhost:8080/api/contacto'; const jsonData=JSON.stringify(data); const dataPost=await fetch(url,{method: 'POST', mode: 'cors', headers:{'Content-Type': 'application/json',}, body: jsonData,}); return dataPost.ok ? 'Usuario guardado' : 'Algo falló';}catch (error){console.log(error);}}; const cleanInputs=()=>{Swal.fire({icon: 'success', title: 'Gracias por inscribirte', showConfirmButton: true,}); nameInput.value=null; lastNameInput.value=null; emailInput.value=null;};};foo();